/** 
 *  @fileOverview Default class for Satviz application. 
 *
 *  @author       Vojtěch Pospíšil
 */

"use strict";

import React, { Component, useEffect } from 'react';
import {
    StyleSheet,
    Text,
    TextInput,
    View,
    Dimensions,
    Button,
    TouchableWithoutFeedback,
    TouchableOpacity,
    FlatList,
    ScrollView,
} from 'react-native';

import {
    ViroARSceneNavigator
} from 'react-viro';

import SplashScreen from 'react-native-splash-screen';
import SlidingPanel from 'react-native-sliding-up-down-panels';
import SectionedMultiSelect from 'react-native-sectioned-multi-select';
import Modal from "react-native-modal";
import IconF from 'react-native-vector-icons/Feather';
import IconM from 'react-native-vector-icons/MaterialIcons'
import Icon from 'react-native-vector-icons/Fontisto';
import Slider from '@react-native-community/slider';

import FlashMessage from "react-native-flash-message";
import { showMessage, hideMessage } from "react-native-flash-message";

import CustomInfoModal from './js/components/CustomInfoModal';

import CoordConverter from './utils/coordsConverter'

import * as satelliteSelectItems from './js/res/selectCategories.json';
import * as groundSegmentSelectItems from './js/res/selectGroundSegment.json';


let InitialARScene = require('./js/ARScene');

/**
 * Default class of Satviz application.
 */
export default class satviz extends Component {
    constructor() {
        super();

        /**
         * Reference to text input for manual satellite selection
         * @type {React.RefObject<any>} */
        this.manualSatSelectTextInput = React.createRef();

        this.state = {
            /** Selected satellite IDs from satellite multislect
             *  @type {Array.<string>} */
            selectedItems: [],
            /** Indication of reaching maximum of selcted items in select 
             * @type {boolean} */
            maxItems: false,

            /** Selected satellite IDs from manual select 
             * @type {Array.<string>} */
            selectedItemsManual: [],

            /** Selected GPS ground segment IDS from GS multiselect 
             * @type {Array.<string>} */
            selectedItemsGroundSegment: [],

            /** Visibility of help modal window 
             * @type {boolean} */
            helpModalVisible: false,

            /** Visibility of satellite info modal (CustomInfoModal) 
             * @type {boolean} */
            satelliteModalVisible: false,
            /** Selected satellite which info is displayed in info modal 
             * @type {SatelliteObject} */
            satelliteModalSatellite: null,

            /** IDs of satellites with enabled orbit rendering 
             * @type {Array.<string>} */
            orbitIDs: [],
            /** Opacity of orbit line (0 = 100% transparent) 
             * @type {number} */
            orbitOpacity: 0.8,

            /** Visibility of GPS ground segment information modal 
             * @type {boolean}*/
            groundSegmentModalVisible: false,

            /** Visibility of sliding panel 
             * @type {boolean} */
            slidingPanelToggled: false,
            /** Text of toggle part of sliding panel 
             * @type {string} */
            slidingPanelText: "Click to reveal satellite selection!",

            /** Value of time speed slider
             * @type {number} */
            timeSpeedSliderValue: 1,

            /** Application own time
             * @type {Date} */
            appDateTime: new Date(),
        };

        /** Timeout for opacity slider 
         * @type {any} */
        this.opacitySliderTimeout;

        /** Maximum number of selected items in satellite multiselect 
         * @type {number} */
        this.maxSelectedItems = 50;
    }

    componentDidMount = () => {
        // Do stuff while splash screen is shown
        // After having done stuff (such as async tasks) hide the splash screen
        SplashScreen.hide();
    }

    /**
     * Callback for change in satellite multiselect selected items.
     * 
     * @param {Array.<string>} selectedItems IDs of selected items in multiselect
     */
    onSatelliteSelectedItemsChange = (selectedItems) => {
        if (selectedItems.length >= this.maxSelectedItems) {
            if (selectedItems.length === this.maxSelectedItems) {
                this.setState({ selectedItems })
            }

            this.setState({
                maxItems: true,
            })
            return;
        }

        this.setState({
            selectedItems,
            maxItems: false,
        })
    }

    /**
     * Callback for change in ground segment multiselect selected items.
     * 
     * @param {Array.<string>} selectedItemsGroundSegment IDs of selected items in multiselect
     */
    onGroundSegmentSelectedItemsChange = (selectedItemsGroundSegment) => {
        this.setState({ selectedItemsGroundSegment });
    };

    /**
     *  Change visibility of help modal
     */
    toggleHelpModal = () => {
        this.setState({ helpModalVisible: !this.state.helpModalVisible });
    };

    /**
     * Callback for setting satellite for satellite info modal.
     * 
     * @param {SatelliteObject} sat Satellite to be displayed info about
     */
    satelliteModalSetSatelliteCallback = (sat) => {
        this.setState({
            satelliteModalSatellite: sat,
        });

        this.toggleSatelliteModal();
    }

    /**
     * Callback function for setting current app time in this class.
     * 
     * @param {Date} datetime JS Date object
     */
    setDateTimeCallback = (datetime) => {
        this.setState({
            appDateTime: datetime
        });
    }

    /**
     * Function for getting current app date in format for display.
     * 
     * @returns {string} Current app date and time
     */
    getDateForDisplay = () => {
        let year = this.state.appDateTime.getFullYear();
        let month = (1 + this.state.appDateTime.getMonth()).toString().padStart(2, '0');
        let day = this.state.appDateTime.getDate().toString().padStart(2, '0');
        let date =  month + '/' + day + '/' + year;

        let hours = this.state.appDateTime.getHours();
        let minutes = this.state.appDateTime.getMinutes();
        let time = hours + ":"+ minutes;

        return date + " " + time;
    }

    /**
     * Change visibility of satellite modal.
     */
    toggleSatelliteModal = () => {
        this.setState({
            satelliteModalVisible: !this.state.satelliteModalVisible,
        });
    };

    /**
     * Change visibility of GPS ground segment modal.
     */
    toggleGroundSegmentModal = () => {
        this.setState({
            groundSegmentModalVisible: !this.state.groundSegmentModalVisible,
        });
    }

    /**
     * Change visibility of sliding panel and change appropriately toggle text.
     */
    toggleSlidePanel = () => {
        if (this.state.slidingPanelToggled) {
            this.setState({ slidingPanelText: "Click to show satellite selection!" });
        } else {
            this.setState({ slidingPanelText: "Click to hide satellite selection!" });
        }

        this.setState({ slidingPanelToggled: !this.state.slidingPanelToggled });
    };

    /**
     * Adds satellite manually with ID in manualSatSelectTextInput.
     */
    addManual = () => {
        // Check whether user added valid input
        if (this.manualSatSelectTextInput.current._lastNativeText === undefined ||
            isNaN(this.manualSatSelectTextInput.current._lastNativeText) ||
            this.manualSatSelectTextInput.current._lastNativeText.trim() == "") {
                showMessage({
                    message: "Satellite ID is 5 digit number only.",
                    type: "danger",
                })

                this.manualSatSelectTextInput.current.clear();
                return;
        }

        // Add trailing zeros where the number is shorter than 5 chars
        let userInput = this.manualSatSelectTextInput.current._lastNativeText;
        if (userInput.length < 5) {
            userInput = ("00000" + userInput).slice(-5);
        }

        if (this.state.selectedItemsManual.includes(userInput) === false) {
            this.setState({ selectedItemsManual: [].concat(this.state.selectedItemsManual).concat(userInput) });
        } else {
            showMessage({
                message: "Satellite with this ID is already selected!",
                type: "danger",
            });
        }

        this.manualSatSelectTextInput.current.clear();
    }

    /**
     * Removes satellite manually with given ID.
     * 
     * @param {string} removeID ID to be removed from manualy selected IDs
     */
    removeManual = (removeID) => {
        this.setState({ selectedItemsManual: this.state.selectedItemsManual.filter(id => id != removeID) })
    }

    /**
     * Removes all manualy selected satellites.
     */
    removeManualAll = () => {
        this.setState({ selectedItemsManual: [] });
    }

    /**
     * Removes satellite with given ID from selected satellites.
     * 
     * @param {string} satID ID of satellite to be removed
     */
    removeSatelliteWithError = (satID) => {
        // Try to remove from selected in category
        if (this.state.selectedItems.includes(satID)) {
            let arr = this.state.selectedItems.filter(e => e !== satID);

            this.setState({ selectedItems: arr });
        }

        // Try to remove from selected manually
        if (this.state.selectedItemsManual.includes(satID)) {
            let arr = this.state.selectedItemsManual.filter(e => e !== satID);

            this.setState({ selectedItemsManual: arr });
        }

        showMessage({
            message: "Satellite with ID " + satID + " had to be removed from your selection because of internal problem with calculating its position.",
            type: "warning",
        });
    }

    /**
     * Get orbit status for satellite in Satellite Info Modal.
     */
    isOrbitEnabledForSatelliteInModal = () => {
        // No sat selected protection
        if (this.state.satelliteModalSatellite === null) {
            return false;
        }

        return this.state.orbitIDs.includes(this.state.satelliteModalSatellite.id);
    }

    /**
     * Change visibility of orbit ofsatellite with given ID.
     * 
     * @param {string} satelliteID Satellite which orbit we want to toggle
     */
    toggleOrbitVisibility = (satelliteID) => {
        // No sat selected protection
        if (this.state.satelliteModalSatellite === null) {
            return;
        }

        if (this.state.orbitIDs.includes(satelliteID)) {
            let arr = this.state.orbitIDs.filter(id => id != satelliteID);

            this.setState({
                orbitIDs: arr,
            });
        } else {
            let current = [...this.state.orbitIDs];
            this.setState({
                orbitIDs: [...current, satelliteID],
            });
        }
    }

    /**
     * Renders main window of the Satviz application.
     * 
     * @returns {View} Top level View node
     */
    render() {
        return (
            <View style={styles.container}>

                <ViroARSceneNavigator
                    style={styles.arView}
                    autofocus={true}
                    shadowsEnabled={true}
                    initialScene={{ scene: InitialARScene }}
                    viroAppProps={{
                        satelliteClickCallback: this.satelliteModalSetSatelliteCallback,
                        satelliteIDs: [].concat(this.state.selectedItems).concat(this.state.selectedItemsManual),
                        groundSegmentIDs: this.state.selectedItemsGroundSegment,
                        orbitIDs: this.state.orbitIDs,
                        orbitOpacity: this.state.orbitOpacity,
                        timeScale: this.state.timeSpeedSliderValue,
                        removeSatelliteCallback: this.removeSatelliteWithError,
                        setDateTimeCallback: this.setDateTimeCallback,
                    }}
                />

                <Modal
                    isVisible={this.state.helpModalVisible}
                    useNativeDriver={true}
                    onBackdropPress={this.toggleHelpModal}
                >
                    <View style={styles.helpModal}>
                        <TouchableOpacity onPress={this.toggleHelpModal} style={styles.modalCloseIcon}>
                            <Icon name="close-a" size={20} color="grey" />
                        </TouchableOpacity>

                        <Text style={styles.helpModalHeading}>About</Text>
                        <Text style={{ textAlign: 'justify' }}>This application is used to visualize satellites orbiting the Earth in augmented reality. Along with the orbit visualization, it allows the display of basic satellite information (position, speed, ...) as well as the display of positions and information regarding the terrestrial GPS segment. </Text>


                        <Text style={[styles.helpModalHeading, { marginTop: 10, textAlign: 'center' }]}>How To</Text>
                        <Text>1. Detect target   </Text>
                        <Text style={{ textAlign: 'center' }}>2. Choose satellite or ground segment element from slide-up menu   </Text>
                        <Text style={{ textAlign: 'center' }}>3. Click on satellites to get more info   </Text>


                        <Text style={[styles.helpModalHeading, { marginTop: 10 }]}>Settings</Text>
                        <Text>Orbit opacity: </Text>
                        <Slider
                            value={this.state.orbitOpacity}
                            minimumValue={0}
                            maximumValue={1}
                            onValueChange={(val) => {
                                clearTimeout(this.opacitySliderTimeout);
                                this.opacitySliderTimeout = setTimeout(() => {
                                    this.setState({ orbitOpacity: val })
                                }, 100)
                            }
                            }
                            style={{ width: '90%' }}
                        />
                    </View>
                </Modal>

                <TouchableOpacity onPress={this.toggleHelpModal} style={styles.modalIcon}>
                    <IconM name="help-outline" size={30} color="white" style={styles.iconShadow} />
                </TouchableOpacity>

                <View style={styles.bodyViewStyle}>
                    <CustomInfoModal
                        satellite={this.state.satelliteModalSatellite}
                        isModalVisible={this.state.satelliteModalVisible}
                        closeModal={() => this.toggleSatelliteModal()}
                        orbitEnabled={this.isOrbitEnabledForSatelliteInModal()}
                        orbitButtonCallback={this.toggleOrbitVisibility}
                    />
                </View>

                <SlidingPanel
                    headerLayoutHeight={50}
                    onAnimationStop={this.toggleSlidePanel}
                    headerLayout={() =>
                        <View style={styles.headerLayoutStyle}>
                            <Text style={styles.commonTextStyle}>{this.state.slidingPanelText}</Text>
                        </View>
                    }
                    allowDragging={true}
                    slidingPanelLayout={() =>
                        <View style={styles.slidingPanelLayoutStyle}>
                            <View style={{ flex: 1 }}>
                                <ScrollView style={{ flex: 1 }}>
                                    <View
                                        style={{
                                            flex: 1,
                                            flexDirection: 'row',
                                            flexWrap: 'wrap',
                                            alignItems: 'center',
                                            marginVertical: 10,
                                        }}
                                    >
                                        <TextInput
                                            style={{
                                                height: 40,
                                                width: '70%',
                                                paddingLeft: '3%',
                                            }}
                                            placeholder="Choose by typing satellite ID"
                                            ref={this.manualSatSelectTextInput}
                                            keyboardType='numeric'
                                            value={this.state.text}
                                        />
                                        <View style={{ width: '20%' }}>
                                            <Button title="Add!" onPress={this.addManual} />
                                        </View>
                                    </View>

                                    <View
                                        style={{
                                            flexWrap: 'wrap',
                                            alignItems: 'center',
                                            justifyContent: 'flex-start',
                                            flexDirection: 'row',
                                        }}
                                    >
                                        {
                                            this.state.selectedItemsManual.length > 1 ? (
                                                <View
                                                    style={{
                                                        overflow: 'hidden',
                                                        justifyContent: 'center',
                                                        height: 34,
                                                        borderColor: colors.selectedBubble,
                                                        flexDirection: 'row',
                                                        alignItems: 'center',
                                                        paddingLeft: 10,
                                                        margin: 3,
                                                        paddingTop: 0,
                                                        paddingRight: 10,
                                                        paddingBottom: 0,
                                                        borderRadius: 20,
                                                        borderWidth: 1,
                                                    }}
                                                >
                                                    <TouchableOpacity
                                                        onPress={() => {
                                                            this.removeManualAll()
                                                        }}
                                                        style={{
                                                            borderTopRightRadius: 20,
                                                            borderBottomRightRadius: 20,
                                                        }}
                                                    >
                                                        <Text
                                                            style={{
                                                                color: colors.selectedBubble,
                                                                fontSize: 13,
                                                                marginRight: 0,
                                                            }}
                                                        >
                                                            Remove All
                                  </Text>
                                                    </TouchableOpacity>
                                                </View>
                                            ) : null
                                        }

                                        {
                                            this.state.selectedItemsManual.map((item, key) => (
                                                <View
                                                    style={{
                                                        overflow: 'hidden',
                                                        justifyContent: 'center',
                                                        height: 34,
                                                        borderWidth: 1,
                                                        borderRadius: 20,
                                                        borderColor: colors.selectedBubble,
                                                        flexDirection: 'row',
                                                        alignItems: 'center',
                                                        paddingLeft: 10,
                                                        margin: 3,
                                                        paddingTop: 0,
                                                        paddingRight: 0,
                                                        paddingBottom: 0,
                                                        color: colors.selectedBubble,
                                                    }}
                                                    key={key}
                                                >
                                                    <Text
                                                        numberOfLines={1}
                                                        style={{
                                                            fontSize: 13,
                                                            marginRight: 0,
                                                            color: colors.selectedBubble,
                                                        }}
                                                    >
                                                        {item}
                                                    </Text>
                                                    <TouchableOpacity
                                                        onPress={() => this.removeManual(item)}
                                                        style={{
                                                            borderTopRightRadius: 20,
                                                            borderBottomRightRadius: 20,
                                                        }}
                                                    >
                                                        <IconF
                                                            name="x"
                                                            style={{
                                                                fontSize: 16,
                                                                marginHorizontal: 6,
                                                                marginVertical: 7,
                                                                color: colors.selectedBubble,
                                                            }}
                                                        />
                                                    </TouchableOpacity>
                                                </View>
                                            ))
                                        }
                                    </View>

                                    <View style={styles.hairlineSplitLine} />

                                    <View style={{ flex: 1, justifyContent: 'center' }}>
                                        <SectionedMultiSelect
                                            items={satelliteSelectItems.default}
                                            uniqueKey="id"
                                            subKey="children"
                                            selectText="Choose satellites from catogories"
                                            searchPlaceholderText="Search by name..."
                                            showDropDowns={true}
                                            readOnlyHeadings={false}
                                            selectChildren={true}
                                            showRemoveAll={true}
                                            onSelectedItemsChange={this.onSatelliteSelectedItemsChange}
                                            selectedItems={this.state.selectedItems}
                                            confirmText={`${this.state.maxItems ? 'Max satellites selected' : 'Confirm'}`}
                                        />
                                    </View>

                                    <View style={[styles.hairlineSplitLine]} />

                                    <View style={{ flex: 1, flexDirection: 'row', alignItems: 'center', justifyContent: 'space-between' }}>
                                        <View style={{ width: '80%' }}>
                                            <SectionedMultiSelect
                                                items={groundSegmentSelectItems.default}
                                                uniqueKey="id"
                                                subKey="children"
                                                selectText="Choose ground segments"
                                                searchPlaceholderText="Search by name..."
                                                showDropDowns={false}
                                                readOnlyHeadings={true}
                                                selectChildren={true}
                                                // expandDropDowns={true} // causes weird bug at bottom part of screen
                                                showRemoveAll={true}
                                                onSelectedItemsChange={this.onGroundSegmentSelectedItemsChange}
                                                selectedItems={this.state.selectedItemsGroundSegment}
                                            />
                                        </View>
                                        <View style={{ position: 'absolute', top: 15, right: 25 }}>
                                            <TouchableOpacity onPress={this.toggleGroundSegmentModal}>
                                                <IconF name="info" size={30} color="gray" style={styles.iconShadow} />
                                            </TouchableOpacity>
                                        </View>
                                        <View></View>
                                    </View>


                                    <Modal
                                        isVisible={this.state.groundSegmentModalVisible}
                                        useNativeDriver={true}
                                        onBackdropPress={this.toggleGroundSegmentModal}
                                    >
                                        <View style={styles.groundSegmentModal}>
                                            <View style={{ height: '93%' }}>
                                                <ScrollView>
                                                    <Text style={styles.groundSegmentHeading}>Master Control Stations and Alternate MCS</Text>
                                                    <Text style={styles.groundSegmentColors}>Red and Gold</Text>
                                                    <Text style={styles.groundSegmentText}>The master control station, located at Schriever Air Force Base in Colorado Springs, Colorado, is responsible for overall management of the remote monitoring and transmission sites. GPS ephemeris being a tabulation of computed positions, velocities and derived right ascension and declination of GPS satellites at specific times, replace "position" with "ephemeris" because the Master Control Station computes not only position but also velocity, right ascension and declination parameters for eventual upload to GPS satellites.</Text>

                                                    <Text style={[styles.groundSegmentHeading, { marginTop: 5 }]}>Monitor Stations</Text>
                                                    <Text style={styles.groundSegmentColors}>AFMS blue, AFSCN yellow, NGA purple</Text>
                                                    <Text style={styles.groundSegmentText}>Six monitor stations are located at Schriever Air Force Base in Colorado, Cape Canaveral, Florida, Hawaii, Ascension Island in the Atlantic Ocean, Diego Garcia Atoll in the Indian Ocean, and Kwajalein Island in the South Pacific Ocean.Six additional monitoring stations were added in 2005 in Argentina, Bahrain, United Kingdom, Ecuador, Washington DC, and Australia. Each of the monitor stations checks the exact altitude, position, speed, and overall health of the orbiting satellites. The control segment uses measurements collected by the monitor stations to predict the behavior of each satellite's orbit and clock. The prediction data is up-linked, or transmitted, to the satellites for transmission back to the users. The control segment also ensures that the GPS satellite orbits and clocks remain within acceptable limits. A station can track up to 11 satellites at a time. This "check-up" is performed twice a day, by each station, as the satellites complete their journeys around the earth. Noted variations, such as those caused by the gravity of the moon, sun and the pressure of solar radiation, are passed along to the master control station.</Text>

                                                    <Text style={[styles.groundSegmentHeading, { marginTop: 5 }]}>Ground Antennas</Text>
                                                    <Text style={styles.groundSegmentColors}>Green</Text>
                                                    <Text style={styles.groundSegmentText}>
                                                        The Ground Antennas uplink data to the satellites via S-band radio signals. These data includes ephemerides and clock correction information transmitted within the Navigation Message, as well as command telemetry from the MCS.
                                                        This information can be uploaded to each satellite three times per day, i.e., every 8 hours; nevertheless, it is usually updated just once a day.
                                                    </Text>
                                                </ScrollView>
                                            </View>
                                            <View style={{ width: '40%', marginTop: 10 }}>
                                                <Button title="Hide info" onPress={this.toggleGroundSegmentModal} />
                                            </View>
                                        </View>
                                    </Modal>

                                    <View style={[styles.hairlineSplitLine]} />

                                    <View style={{ marginHorizontal: 10, marginTop: 10}}>
                                        
                                        <View style={{flex: 1, flexDirection: "row"}}>
                                            <View style={{width: "80%"}}>
                                                <Text style={{paddingBottom: 5, color: "black"}}>Current date: <Text style={{fontWeight: "bold"}}>{this.getDateForDisplay()}</Text></Text>
                                                <Text style={{color: "black"}}>Set time speed ({Math.trunc(this.state.timeSpeedSliderValue).toString()}x normal)</Text>
                                        
                                                <Slider
                                                    value={this.state.timeSpeedSliderValue}
                                                    minimumValue={-10000}
                                                    maximumValue={10000}
                                                    step={500}
                                                    onValueChange={(val) => this.setState({ timeSpeedSliderValue: val })}
                                                />
                                            </View>
                                            
                                            <View style={{height: "50%", marginTop: 15}}>
                                                <Button 
                                                    title="Reset"
                                                    onPress={() => this.setState({ timeSpeedSliderValue: 1 })}
                                                />
                                            </View>
                                        </View>
                                        
                                    </View>

                                    <View style={{ paddingBottom: 400 }}></View>
                                </ScrollView>
                            </View>
                        </View>
                    }
                />

                <FlashMessage
                    position={"top"}
                    autoHide={true}
                />
            </View>
        );
    }
}

module.exports = satviz

// Constants used in styles
const { width, height } = Dimensions.get('window');

const colors = {
    selectedBubble: '#848787',
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    bodyViewStyle: {
        flex: 1,
        position: 'absolute',
        justifyContent: 'center',
        alignItems: 'center',
    },
    arView: {
        flex: 1,
        position: 'absolute',
        height: '100%',
    },
    headerLayoutStyle: {
        width,
        height: 50,
        backgroundColor: 'grey',
        justifyContent: 'center',
        alignItems: 'center',
    },
    slidingPanelLayoutStyle: {
        width,
        height,
        backgroundColor: '#ffffff',
    },
    commonTextStyle: {
        color: 'white',
        fontSize: 18,
    },

    modalIcon: {
        position: 'absolute',
        right: '5%',
        top: '2%',
    },
    helpModal: {
        backgroundColor: "white",
        marginHorizontal: '10%',
        padding: 15,
        height: '50%',
        textAlign: 'center',
        alignItems: 'center',
    },
    helpModalHeading: {
        fontWeight: 'bold',
        textAlign: 'center',
        fontSize: 16
    },
    modalCloseIcon: {
        position: 'absolute',
        right: 9,
        top: 9,
    },

    groundSegmentModal: {
        backgroundColor: "white",
        marginHorizontal: '10%',
        height: '80%',
        textAlign: 'right',
        alignItems: 'center',
        padding: '3%',
        paddingBottom: 0,
    },
    groundSegmentHeading: {
        fontWeight: 'bold',
        textAlign: 'center',
        fontSize: 16
    },
    groundSegmentText: {
        textAlign: 'justify',
    },
    groundSegmentColors: {
        textAlign: 'center',
        fontSize: 12
    },

    hairlineSplitLine: {
        borderBottomColor: 'black',
        borderBottomWidth: StyleSheet.hairlineWidth,
    },

    iconShadow: {
        shadowOpacity: 2,
        textShadowRadius: 2,
        textShadowOffset: { width: 0, height: 0 }
    },
});

(function(){
  function toggle(ev) {
    var button = ev.target;
    var parent = ev.target.parentElement;
    while(parent) {
      if (parent.tagName === 'TABLE' && parent.classList.contains('summary')) break;
      parent = parent.parentElement;
    }

    if (!parent) return;

    var tbody = parent.querySelector('tbody');
    if (button.classList.contains('opened')) {
      button.classList.remove('opened');
      button.classList.add('closed');
      tbody.style.display = 'none';
    } else {
      button.classList.remove('closed');
      button.classList.add('opened');
      tbody.style.display = 'block';
    }
  }

  var buttons = document.querySelectorAll('.inherited-summary thead .toggle');
  for (var i = 0; i < buttons.length; i++) {
    buttons[i].addEventListener('click', toggle);
  }
})();

// inner link(#foo) can not correctly scroll, because page has fixed header,
// so, I manually scroll.
(function(){
  var matched = location.hash.match(/errorLines=([\d,]+)/);
  if (matched) return;

  function adjust() {
    window.scrollBy(0, -55);
    var el = document.querySelector('.inner-link-active');
    if (el) el.classList.remove('inner-link-active');

    // ``[ ] . ' " @`` are not valid in DOM id. so must escape these.
    var id = location.hash.replace(/([\[\].'"@$])/g, '\\$1');
    var el = document.querySelector(id);
    if (el) el.classList.add('inner-link-active');
  }

  window.addEventListener('hashchange', adjust);

  if (location.hash) {
    setTimeout(adjust, 0);
  }
})();

(function(){
  var els = document.querySelectorAll('[href^="#"]');
  var href = location.href.replace(/#.*$/, ''); // remove existed hash
  for (var i = 0; i < els.length; i++) {
    var el = els[i];
    el.href = href + el.getAttribute('href'); // because el.href is absolute path
  }
})();

(function(){
  var matched = location.pathname.match(/\/(manual\/.*\.html)$/);
  if (!matched) return;

  var currentName = matched[1];
  var cssClass = '.navigation .manual-toc li[data-link="' + currentName + '"]';
  var styleText = cssClass + '{ display: block; }\n';
  styleText += cssClass + '.indent-h1 a { color: #039BE5 }';
  var style = document.createElement('style');
  style.textContent = styleText;
  document.querySelector('head').appendChild(style);
})();

(function(){
  if (location.protocol === 'file:') {
    var elms = document.querySelectorAll('a[href="./"]');
    for (var i = 0; i < elms.length; i++) {
      elms[i].href = './index.html';
    }
  }
})();

(function(){
  prettyPrint();
  var lines = document.querySelectorAll('.prettyprint.linenums li[class^="L"]');
  for (var i = 0; i < lines.length; i++) {
    lines[i].id = 'lineNumber' + (i + 1);
  }

  var matched = location.hash.match(/errorLines=([\d,]+)/);
  if (matched) {
    var lines = matched[1].split(',');
    for (var i = 0; i < lines.length; i++) {
      var id = '#lineNumber' + lines[i];
      var el = document.querySelector(id);
      el.classList.add('error-line');
    }
    return;
  }

  if (location.hash) {
    // ``[ ] . ' " @`` are not valid in DOM id. so must escape these.
    var id = location.hash.replace(/([\[\].'"@$])/g, '\\$1');
    var line = document.querySelector(id);
    if (line) line.classList.add('active');
  }
})();

(function(){
  var searchIndex = window.esdocSearchIndex;
  var searchBox = document.querySelector('.search-box');
  var input = document.querySelector('.search-input');
  var result = document.querySelector('.search-result');
  var selectedIndex = -1;
  var prevText;

  // active search box and focus when mouse enter on search box.
  searchBox.addEventListener('mouseenter', function(){
    searchBox.classList.add('active');
    input.focus();
  });

  // search with text when key is upped.
  input.addEventListener('keyup', function(ev){
    var text = ev.target.value.toLowerCase();
    if (!text) {
      result.style.display = 'none';
      result.innerHTML = '';
      return;
    }

    if (text === prevText) return;
    prevText = text;

    var html = {class: [], method: [], member: [], function: [], variable: [], typedef: [], external: [], file: [], test: [], testFile: []};
    var len = searchIndex.length;
    var kind;
    for (var i = 0; i < len; i++) {
      var pair = searchIndex[i];
      if (pair[0].indexOf(text) !== -1) {
        kind = pair[3];
        html[kind].push('<li><a href="' + pair[1] + '">' + pair[2] + '</a></li>');
      }
    }

    var innerHTML = '';
    for (kind in html) {
      var list = html[kind];
      if (!list.length) continue;
      innerHTML += '<li class="search-separator">' + kind + '</li>\n' + list.join('\n');
    }
    result.innerHTML = innerHTML;
    if (innerHTML) result.style.display = 'block';
    selectedIndex = -1;
  });

  // down, up and enter key are pressed, select search result.
  input.addEventListener('keydown', function(ev){
    if (ev.keyCode === 40) {
      // arrow down
      var current = result.children[selectedIndex];
      var selected = result.children[selectedIndex + 1];
      if (selected && selected.classList.contains('search-separator')) {
        var selected = result.children[selectedIndex + 2];
        selectedIndex++;
      }

      if (selected) {
        if (current) current.classList.remove('selected');
        selectedIndex++;
        selected.classList.add('selected');
      }
    } else if (ev.keyCode === 38) {
      // arrow up
      var current = result.children[selectedIndex];
      var selected = result.children[selectedIndex - 1];
      if (selected && selected.classList.contains('search-separator')) {
        var selected = result.children[selectedIndex - 2];
        selectedIndex--;
      }

      if (selected) {
        if (current) current.classList.remove('selected');
        selectedIndex--;
        selected.classList.add('selected');
      }
    } else if (ev.keyCode === 13) {
      // enter
      var current = result.children[selectedIndex];
      if (current) {
        var link = current.querySelector('a');
        if (link) location.href = link.href;
      }
    } else {
      return;
    }

    ev.preventDefault();
  });

  // select search result when search result is mouse over.
  result.addEventListener('mousemove', function(ev){
    var current = result.children[selectedIndex];
    if (current) current.classList.remove('selected');

    var li = ev.target;
    while (li) {
      if (li.nodeName === 'LI') break;
      li = li.parentElement;
    }

    if (li) {
      selectedIndex = Array.prototype.indexOf.call(result.children, li);
      li.classList.add('selected');
    }
  });

  // clear search result when body is clicked.
  document.body.addEventListener('click', function(ev){
    selectedIndex = -1;
    result.style.display = 'none';
    result.innerHTML = '';
  });

})();

(function(){
  function toggle(ev) {
    var button = ev.target;
    var parent = ev.target.parentElement;
    while(parent) {
      if (parent.tagName === 'TR' && parent.classList.contains('test-interface')) break;
      parent = parent.parentElement;
    }

    if (!parent) return;

    var direction;
    if (button.classList.contains('opened')) {
      button.classList.remove('opened');
      button.classList.add('closed');
      direction = 'closed';
    } else {
      button.classList.remove('closed');
      button.classList.add('opened');
      direction = 'opened';
    }

    var targetDepth = parseInt(parent.dataset.testDepth, 10) + 1;
    var nextElement = parent.nextElementSibling;
    while (nextElement) {
      var depth = parseInt(nextElement.dataset.testDepth, 10);
      if (depth >= targetDepth) {
        if (direction === 'opened') {
          if (depth === targetDepth)  nextElement.style.display = '';
        } else if (direction === 'closed') {
          nextElement.style.display = 'none';
          var innerButton = nextElement.querySelector('.toggle');
          if (innerButton && innerButton.classList.contains('opened')) {
            innerButton.classList.remove('opened');
            innerButton.classList.add('closed');
          }
        }
      } else {
        break;
      }
      nextElement = nextElement.nextElementSibling;
    }
  }

  var buttons = document.querySelectorAll('.test-summary tr.test-interface .toggle');
  for (var i = 0; i < buttons.length; i++) {
    buttons[i].addEventListener('click', toggle);
  }

  var topDescribes = document.querySelectorAll('.test-summary tr[data-test-depth="0"]');
  for (var i = 0; i < topDescribes.length; i++) {
    topDescribes[i].style.display = '';
  }
})();

/** 
 *  @fileOverview Default AR scene of Satviz application. 
 *
 *  @author       Vojtěch Pospíšil
 */

"use strict";

import React, { Component } from 'react';

import { StyleSheet } from 'react-native';

import {
    ViroARScene,
    ViroConstants,
    ViroARImageMarker,
    ViroARTrackingTargets,
    ViroSpotLight,
    ViroNode,
    ViroMaterials,
    ViroSphere,
} from 'react-viro';

import Globe from './components/Globe'
import { showMessage, hideMessage } from "react-native-flash-message";

/**
 * Class representing default AR scene.
 */
export default class ARScene extends Component {
    constructor() {
        super();


        this.state = {
            /** Rotation of globe based on real world 
             * @type {Array.<number>} */
            globeRotation: [0, 0, 0],
            /** State of detection true=globe target detected
             * @type {boolean} */
            globeDetected: false,
            /** Detected globe real world position 
             * @type {Array.<number>|null} */
            globePosition: null,

            /** IDs of selected satellites to render 
             * @type {Array.<string>} */
            satelliteIDs: [],

            /** IDs of satellites which should have orbit rendered 
             * @type {Array.<string>} */
            orbitIDs: [],
            /** Opacity of rendered orbits 
             * @type {number} */
            orbitOpacity: 0.8,

            /** Scale (multiply) of real time speed 
             * @type {number} */
            timeScale: 1
        };

        /** Names of currently tracked image targets 
         * @type {Array.<string>} */
        this.tracking = [];
        /** State of globe tracking 
         * @type {boolean} */
        this.isTracking = false;

        /** Count of globe position recalculation 
         * @type {number} */
        this.positionModCount = 0;

        /** Switch for Globe rendering 
         * @type {boolean} */
        this.renderDisabled = true;
    }

    componentDidUpdate(prevProps, prevState) {
        // Rerender on change of props
        if (this.state.satelliteIDs != this.props.arSceneNavigator.viroAppProps.satelliteIDs) {
            this.setState({
                satelliteIDs: this.props.arSceneNavigator.viroAppProps.satelliteIDs,
            })
        }
        if (this.state.timeScale != this.props.arSceneNavigator.viroAppProps.timeScale) {
            this.setState({
                timeScale: this.props.arSceneNavigator.viroAppProps.timeScale,
            })
        }
        if (this.state.orbitIDs != this.props.arSceneNavigator.viroAppProps.orbitIDs) {
            this.setState({
                orbitIDs: this.props.arSceneNavigator.viroAppProps.orbitIDs,
            });
        }
        if (this.state.orbitOpacity != this.props.arSceneNavigator.viroAppProps.orbitOpacity) {
            this.setState({
                orbitOpacity: this.props.arSceneNavigator.viroAppProps.orbitOpacity,
            });
        }
    }

    /**
     * Calculate globe rotation from tracking targets
     * 
     * @returns {Array.<number>} Rotation in format [0, Y rotation, 0]
     */
    getGlobeRotation = () => {
        if (this.tracking.length === 0) {
            return this.state.globeRotation;
        }

        let sum = 0;
        this.tracking.forEach(element => {
            // -15 is because of "default pose" of globe where africa is in front of camera
            sum += (angles[element] - 15);
        });

        let angle = sum / this.tracking.length;

        // Negative angle is because rotation has opposite direction than angles on globe
        return [0, -angle, 0];
    }

    /**
     * Modify globe position based on given position.
     * 
     * @param {Array.<number>} position Position to adjust globe position to
     */
    modifyGlobePosition = (position) => {
        // move Z coord into center of globe
        position[2] = position[2] - 0.085;

        if (this.positionModCount === 0) {
            this.setState({ globePosition: position, });
        } else {
            // Limit position modification to only first 4 attempts
            if (this.positionModCount > 3) {
                return;
            }

            let newPos = [];
            newPos[0] = ((this.state.globePosition[0] * this.positionModCount) + position[0]) / (this.positionModCount + 1);
            newPos[1] = ((this.state.globePosition[1] * this.positionModCount) + position[1]) / (this.positionModCount + 1);
            newPos[2] = ((this.state.globePosition[2] * this.positionModCount) + position[2]) / (this.positionModCount + 1);

            /*
           newPos[0] = ((this.state.globePosition[0]) + position[0]) / 2;
           newPos[1] = ((this.state.globePosition[1]) + position[1]) / 2;
           newPos[2] = ((this.state.globePosition[2]) + position[2]) / 2;
           */

            this.setState({ globePosition: newPos, });
        }

        this.positionModCount += 1;
    }

    /**
     * Function which is triggered everytime when imageTarget anchor changes.
     * 
     * @param {any} e Event which happened
     * @param {string} targetName Name of updated target
     */
    _onAnchorUpdated = (e, targetName) => {
        let changed = false;

        if (e.trackingMethod === "tracking") {
            // Tracking something - render Globe
            this.renderDisabled = false;

            // On flat target simply copy detected position and rotation
            if (targetName === "flatTarget") {
                this.setState({ globePosition: e.position, globeRotation: e.rotation });
                return;
            }

            if (!this.state.globeDetected && targetName !== "flatTarget") {
                this.setState({ globeDetected: true });
            }

            // Add target name to tracking if not there
            if (!this.tracking.includes(targetName)) {
                this.tracking.push(targetName);

                // Hold only last 3 targets
                if (this.tracking.length > 3) {
                    this.tracking.shift();
                }

                changed = true;
            }

            // Update globe position according to detected position
            this.modifyGlobePosition(e.position);
        } else if (e.trackingMethod === "lastKnownPose") {
            // LastKnownPose means that target is no longer available so remove it
            if (this.tracking.includes(targetName)) {
                this.tracking = this.tracking.filter(e => e !== targetName);
                changed = true;
            }
        }

        // When some target has been changed get user know it and update rotation
        if (changed) {
            if (this.tracking.length == 0 && this.isTracking == true) {
                this.isTracking = false;
                showMessage({
                    message: "Tracking lost :(",
                    type: "warning",
                });
            }

            if (this.tracking.length != 0 && this.isTracking == false) {
                this.isTracking = true;
                showMessage({
                    message: "Tracking established",
                    type: "success",
                });
            }

            let rotation = this.getGlobeRotation();
            this.setState({ globeRotation: rotation });
        }
    }

    /**
     * Maps all imageTarget names to ViroARImageMarker
     * 
     * @returns {Array.<ViroARImageMarker>} Array of AR image markers
     */
    renderTargets = () => {
        let targets = [];

        targets = targetNames.map((name) => {
            return (
                <ViroARImageMarker
                    key={name}
                    target={name}
                    onAnchorUpdated={(e) => this._onAnchorUpdated(e, name)}
                />
            )
        });

        return targets;
    }

    /**
     * Returns Globe component with everything set up to visualization
     * 
     * @returns {ViroNode} Globe with all properties set
     */
    renderGlobe = () => {
        if (this.renderDisabled) {
            return (<ViroNode></ViroNode>);
        }

        let position = this.state.globePosition;
        let flatTarget = true;
        let rotation = this.state.globeRotation;

        if (this.state.globeDetected) {
            flatTarget = false;
        }

        return (
            <ViroNode position={position} rotation={rotation}>
                <Globe
                    satelliteClickCallback={this.props.arSceneNavigator.viroAppProps.satelliteClickCallback}
                    satelliteIDs={this.state.satelliteIDs}
                    groundSegmentIDs={this.props.arSceneNavigator.viroAppProps.groundSegmentIDs}
                    timeScale={this.state.timeScale}
                    flatTarget={flatTarget}
                    renderVirtualGlobe={false}
                    removeSatelliteCallback={this.props.arSceneNavigator.viroAppProps.removeSatelliteCallback}
                    orbitIDs={this.state.orbitIDs}
                    orbitOpacity={this.state.orbitOpacity}
                    setDateTimeCallback={this.props.arSceneNavigator.viroAppProps.setDateTimeCallback}
                />
            </ViroNode>
        );
    }

    /**
     * Renders AR scene.
     * 
     * @returns {ViroARScene} ARscene with globe visualization 
     */
    render() {
        return (
            <ViroARScene onTrackingUpdated={this._onInitialized} >

                <ViroSpotLight innerAngle={5} outerAngle={90} direction={[0, -1, -.2]}
                    position={[0, 3, 1]} color="#ffffff" castsShadow={true} />

                <ViroARImageMarker target={"flatTarget"} onAnchorUpdated={(e) => this._onAnchorUpdated(e, "flatTarget")} />

                {this.renderTargets()}

                {this.renderGlobe()}
            </ViroARScene>
        );
    }
}

/**
 * Names of imageTargets of globe
 * @constant
 * @type {Array.<string>}
 */
const targetNames = [
    "africa", "atlantic", "australia", "china", "europe",
    "hawaii", "indonesia", "northAmerica", "southAmerica"
];

/**
 * Angles of rotation of center of imageTarget
 * @constant
 * @type {Object<string, number>}
 */
const angles = {
    "africa": 15,
    "atlantic": 340,
    "australia": 135,
    "china": 90,
    "europe": 15,
    "hawaii": 195,
    "indonesia": 140,
    "northAmerica": 260,
    "southAmerica": 300,
}

// Definition of tracking targets for ARImageTargets
ViroARTrackingTargets.createTargets({
    "flatTarget": {
        source: require('./res/targets/earthFlat.jpeg'),
        orientation: "Up",
        physicalWidth: 0.2 // real world width in meters
    },
    "africa": {
        source: require('./res/targets/africa.jpg'),
        orientation: "Up",
        physicalWidth: 0.3,
    },
    "atlantic": {
        source: require('./res/targets/atlantic.jpg'),
        orientation: "Up",
        physicalWidth: 0.3,
    },
    "australia": {
        source: require('./res/targets/australia.jpg'),
        orientation: "Up",
        physicalWidth: 0.3,
    },
    "china": {
        source: require('./res/targets/china.jpg'),
        orientation: "Up",
        physicalWidth: 0.3,
    },
    "europe": {
        source: require('./res/targets/europe.jpg'),
        orientation: "Up",
        physicalWidth: 0.3,
    },
    "hawaii": {
        source: require('./res/targets/hawaii.jpg'),
        orientation: "Up",
        physicalWidth: 0.3,
    },
    "indonesia": {
        source: require('./res/targets/indonesia.jpg'),
        orientation: "Up",
        physicalWidth: 0.3,
    },
    "northAmerica": {
        source: require('./res/targets/northAmerica.jpg'),
        orientation: "Up",
        physicalWidth: 0.3,
    },
    "southAmerica": {
        source: require('./res/targets/southAmerica.jpg'),
        orientation: "Up",
        physicalWidth: 0.3,
    },
});

ViroMaterials.createMaterials({
    mat: {
        lightingModel: "Blinn",
        diffuseColor: '#6e6e6e'
    }
});

module.exports = ARScene;

/** 
 *  @fileOverview Component for displaying info about selected satellite. 
 *
 *  @author       Vojtěch Pospíšil
 */

"use strict";

import React, { Component } from 'react';

import {
    StyleSheet,
    Text,
    View,
    TouchableOpacity,
    Button
} from 'react-native';

import Modal from "react-native-modal";

import Icon from 'react-native-vector-icons/Fontisto';

/**
* Dictionary of term descriptions.
* @constant
*
* @type {Object<string, string>}
*/
const messageDict = {
    id: "The NORAD Catalog Number or USSPACECOM object number is a sequential 5-digit number assigned by USSPACECOM to all Earth orbiting satellites in order of identification.",
    intlDes: "The International Designator is an naming convention for satellites. It consists of the launch year, a 3-digit incrementing launch number of that year and up to a 3-letter code representing the sequential id of a piece in a launch. Only publicly known satellites are designated.",
    apogee: "Apogee is the point where the satellite is farthest from Earth is called apogee (sometimes called apoapsis, or apifocus",
    perigee: "Perigee is the point where the satellite is closest to the Earth (sometimes called periapsis or perifocus)",
    inclination: "Inclination is the angle between the orbital plane and the equatorial plane. By convention, inclination is a number between 0 and 180 degrees. A satellite in a geostationary orbit has an inclination zero. A satellite in a polar orbit will have an inclination of 90 degrees.",
    latitude: "Latitude is a geographic coordinate that specifies the north–south position of a point on the Earth's surface. It is an angle which ranges from 0° at the Equator to 90° (North or South) at the poles.",
    longitude: "Longitude is a geographic coordinate that specifies the east–west position of a point on the Earth's surface. The longitude of other places is measured as the angle east or west from the Prime Meridian, ranging from 0° at the Prime Meridian to +180° eastward and −180° westward.",
    height: "Altitude or height (sometimes known as 'depth') a distance measurement, usually in the vertical or \"up\" direction, between a reference datum and a point or object. It is basically the distance between object and Earth sea level.",
    velocity: "The velocity of an object is the rate of change of its position with respect to a frame of reference, and is a function of time. It is more known as speed.",
    period: "Period is the amount of time to complete one revolution around the Earth.",
}

/**
* Dictionary of orbit toggle button messages.
* @constant
*
* @type {Object<string, string>}
*/
const message = {
    show: "Show orbit",
    hide: "Hide orbit"
}

/**
 * Component for displaying info about selected satellite.
 * 
 * 
 */
export default class CustomInfoModal extends Component {
    constructor() {
        super();

        this.state = {
            /** Data for rendering information about satellite. @type {Object<string, string>} */
            data: {},
            /** Selected term to be explained. @type {string} */
            explanationRequest: null,
            /** State of component (modal) visibility - `true` means modal is visible @type {boolean} */
            modalVisible: false,
            /** Text of the orbit toggle button. @type {string} */
            buttonText: message.hide,
        };
    }

    componentDidUpdate(prevProps, prevState) {
        // update state to rerender component
        if (this.state.modalVisible != this.props.isModalVisible) {
            this.setState({ modalVisible: this.props.isModalVisible });
        }

        // update info when the sat ID is changed
        if (this.props.satellite && this.state.data.id != this.props.satellite[0].id) {
            this.updateSateliteData();
        }

        // set apropriate text to orbit toggle button
        if (this.props.orbitEnabled != prevProps.orbitEnabled && prevProps.orbitEnabled) {
            if (!this.props.orbitEnabled) {
                this.setState({
                    buttonText: message.hide,
                });
            }

            if (this.props.orbitEnabled) {
                this.setState({
                    buttonText: message.show,
                });
            }
        }
    }

    /**
     * Updates information about satellite.
     */
    updateSateliteData = () => {
        let temp = this.props.satellite[0].getDataForInfoModal();
        this.setState({ data: temp });

        if (this.props.isModalVisible) {
            setTimeout(() => {
                this.updateSateliteData();
            }, 500);
        }
    }

    /**
     * Alternates between toggle orbit button messages.
     */
    changeText = () => {
        let text;
        if (this.state.buttonText === message.show) {
            text = message.hide;
        } else {
            text = message.show;
        }

        this.setState({
            buttonText: text,
        });
    }

    /**
     * Callback for closing modal window.
     */
    closeModalCallback = () => {
        // next opening of modal will be without any explanation
        this.setState({
            explanationRequest: null,
        });

        this.props.closeModal();
    }

    /**
     * Renders text info with satellite informations.
     * 
     * @returns {View} All satellite informations
     */
    renderTextInfo = () => {
        return (
            <View style={styles.textInfo}>
                <Text onPress={() => { this.setState({ explanationRequest: "id" }) }}>
                    <Text style={styles.boldFont}>NORAD ID: </Text>{this.state.data.id}
                </Text>
                <Text onPress={() => { this.setState({ explanationRequest: "intlDes" }) }}>
                    <Text style={styles.boldFont}>Int'l Designator: </Text>{this.state.data.intlDes}
                </Text>
                <Text onPress={() => { this.setState({ explanationRequest: "apogee" }) }}>
                    <Text style={styles.boldFont}>Apogee: </Text>{this.state.data.apogee}
                </Text>
                <Text onPress={() => { this.setState({ explanationRequest: "perigee" }) }}>
                    <Text style={styles.boldFont}>Perigee: </Text>{this.state.data.perigee}
                </Text>
                <Text onPress={() => { this.setState({ explanationRequest: "inclination" }) }}>
                    <Text style={styles.boldFont}>Inclination: </Text>{this.state.data.inclination}
                </Text>
                <Text onPress={() => { this.setState({ explanationRequest: "latitude" }) }}>
                    <Text style={styles.boldFont}>Latitude: </Text>{this.state.data.latitude}
                </Text>
                <Text onPress={() => { this.setState({ explanationRequest: "longitude" }) }}>
                    <Text style={styles.boldFont}>Longitude: </Text>{this.state.data.longitude}
                </Text>
                <Text onPress={() => { this.setState({ explanationRequest: "height" }) }}>
                    <Text style={styles.boldFont}>Height: </Text>{this.state.data.height}
                </Text>
                <Text onPress={() => { this.setState({ explanationRequest: "velocity" }) }}>
                    <Text style={styles.boldFont}>Velocity: </Text>{this.state.data.velocity}
                </Text>
                <Text onPress={() => { this.setState({ explanationRequest: "period" }) }}>
                    <Text style={styles.boldFont}>Period: </Text>{this.state.data.period}
                </Text>
                <Text style={{ fontSize: 10, color: 'gray', marginTop: 5 }}>By clicking on line you can get more info</Text>
            </View>
        );
    }

    /**
     * Renders selected term explanation.
     */
    renderExplanation = () => {
        return (
            <Text style={styles.explanation}>
                {messageDict[this.state.explanationRequest]}
            </Text>
        );
    }

    render() {
        if (!this.state.modalVisible) {
            return <View></View>
        }

        return (
            <View>
                <Modal isVisible={this.state.modalVisible}
                    useNativeDriver={true}
                    onBackdropPress={this.closeModalCallback}
                >
                    <View style={styles.infoModal}>
                        <TouchableOpacity onPress={this.closeModalCallback} style={styles.modalCloseIcon}>
                            <Icon name="close-a" size={20} color="grey" />
                        </TouchableOpacity>

                        {this.renderTextInfo()}

                        <View style={{ marginTop: 10 }}>
                            <Button
                                title={this.state.buttonText}
                                onPress={() => {
                                    this.props.orbitButtonCallback(this.state.data.id);
                                    this.changeText();
                                }}
                            />
                        </View>

                        {this.renderExplanation()}
                    </View>
                </Modal>
            </View>
        );
    }
}

const styles = StyleSheet.create({
    infoModal: {
        backgroundColor: "white",
        marginHorizontal: '10%',
        height: '53%',
        paddingHorizontal: "5%",
    },
    modalCloseIcon: {
        position: 'absolute',
        right: '5%',
        top: '2%',
    },
    infoRow: {
        flex: 1,
        alignSelf: 'stretch',
        flexDirection: 'row',
    },
    rowElement: {
        flex: 1,
        alignSelf: 'stretch'
    },
    boldFont: {
        fontWeight: 'bold',
    },
    textInfo: {
        paddingTop: '5%',
        width: '80%',
    },
    explanation: {
        paddingTop: '3%',
    }
});

/** 
 *  @fileOverview Globe component providing all the visualization.
 *
 *  @author       Vojtěch Pospíšil
 */

"use strict";

import React, { Component } from 'react';

import {
    ViroMaterials,
    ViroNode,
    Viro3DObject,
    ViroAmbientLight,
    ViroSphere,
    ViroBox,
    ViroPolyline,
} from 'react-viro';

import AsyncStorage from '@react-native-community/async-storage';

import SpaceTrack from '../SpaceTrack';
import SatelliteObject from '../SatelliteObject';


var satellite = require('satellite.js');
var Clock = require('timetravel').Clock;

/**
 * Component representing virtual globe with satellites.
 */
export default class Globe extends Component {
    constructor(props) {
        super(props);

        this.state = {
            /** Currently visualized satellite objects
             * @type {Array.<Object>} */
            satellites: [],
            /** Currently visualized satellite IDs
             * @type {Array.<string>} */
            currentSatelliteIDs: [],
            /** IDs of sats without orbit rendered 
             * @type {Array<string>} */
            excludedOrbits: [],
            /** Opacity of orbit line
             * @type {number} */
            orbitOpacity: 0.8,
            /** Rotation around individual axis to compensate globe rotation
             * @type {Array.<number>} */
            groundRotationCompensation: [0, 0, 0]
        }

        /** Compensation of model rotation on different targets
         * @type {Array.<number>} */
        this.modelListRotation = [0, 0, 0];
        if (this.props.flatTarget) {
            this.modelListRotation = [0, -143, 0];
        } else {
            this.modelListRotation = [0, -173, 0];
        }

        /** SpaceTrack object for getting data from Satellite Catalog
         * @type {SpaceTrack} */
        this.ST = new SpaceTrack();

        /** Container for getting TLE records from async function
         * @type {Array.<string>} */
        this.tle = null;

        /** State of globe data loading
         * @type {boolean} */
        this.loading = false;

        /** Number of segments in one orbit
         * @type {number} */
        this.orbitSegmentCount = 100;
        /** Timestamps of orbits starting times 
         * @type {Object<string, any>} */
        this.orbitTimestamps = {};
        /** Points of satellite orbits 
         * @type {Object<string, Array.<Array.<number>>> */
        this.orbitPoints = {};

        /** Globe custom clock with special functions
         * @type {Clock}
         */
        this.clock = new Clock();

        /** Last globe rotation time for optimized rotating
         * @type {Clock.time}
         */
        this.lastRotationTime = this.clock.time();

        /** Timer for satellite motion simulation */
        this.moveTimer = setInterval(this.updatePositions, 100);
        /** Timer for globe rotation simulation */
        this.rotationTimer = setInterval(this.updateRotation, 30);
    }

    async componentDidUpdate(prevProps, prevState) {
        // Fix for problem where numbers starting with zeros was casted to number
        let propIDs;
        if (this.props.satelliteIDs) {
            propIDs = this.props.satelliteIDs.map(id => {
                return ("00000" + id.toString()).slice(-5);
            });
        }

        // Check whether two arrays have same elements or not
        let areIDsSame = (propIDs.length === this.state.currentSatelliteIDs.length) && propIDs.every(value => this.state.currentSatelliteIDs.includes(value));

        if (!areIDsSame && this.loading === false) {
            let addition = propIDs.filter(x => !this.state.currentSatelliteIDs.includes(x));

            let sats = [];

            if (addition.length > 0) {
                // Call ST funtion to get data according to newly added IDs
                this.loading = true;

                this.tle = await this.getTLEsFromWebAndStorage(addition);

                sats = this.parseData();
            }

            // Get satellites which will be not removed from current ones
            let keptFromCurrentObjects = this.state.satellites.filter(sat => propIDs.includes((sat.id).toString()));

            // Get IDs of all visualized satellites
            let curr = [...sats, ...keptFromCurrentObjects].map(sat => (sat.id).toString());

            this.setState({
                satellites: [...sats, ...keptFromCurrentObjects],
                currentSatelliteIDs: curr,
            })
        }

        // Readjust custom clock speed 
        if (this.props.timeScale !== prevProps.timeScale) {
            this.clock.stop();
            this.clock.speed(this.props.timeScale).time(this.clock.time());
            this.clock.start();
        }

        // Update state for component rerender
        if (this.props.orbitIDs !== prevProps.orbitIDs) {
            this.setState({
                excludedOrbits: [...this.props.orbitIDs],
            });
        }
        if (this.props.orbitOpacity !== prevProps.orbitOpacity) {
            this.setState({
                orbitOpacity: this.props.orbitOpacity,
            });
        }
    }

    /**
     * Gets TLEs of satellites with given ids from web service or from async storage.
     * 
     * @param {Array.<string>} ids ID of the satellites for which we want to obtain TLEs
     * 
     * @returns {Array.<string>} TLE lines in format [TLE1 line1, TLE1 line2, TLE2 line1, ...]
     */
    getTLEsFromWebAndStorage = async (ids) => {
        // Get date of satellite tle storage
        let datePairs;
        let additionDateKeys = ids.map(key => key + "Date");
        try {
            datePairs = await AsyncStorage.multiGet(additionDateKeys);
        } catch(e) {
            console.log("Error happened when retrieving dates of TLEs");
            console.log(e);
        }

        let idtoGetFromStorage = [];
        let idToGetFromWeb = [];

        // When date is udefined ->value is not in storage 
        // pair[0] is key, pair[1] is value
        let dateNow = new Date();
        const dayLenghtInMS = 86400000;
        datePairs.forEach(pair => {
            if (pair[1] === null) {
                idToGetFromWeb.push(pair[0]);
            } else {
                let num = Number(pair[1]);
                let dateFromStorage = new Date(num);
                let diff = dateNow.getTime() - dateFromStorage.getTime();

                if (diff > dayLenghtInMS) {
                    idToGetFromWeb.push(pair[0]);
                } else {
                    idtoGetFromStorage.push(pair[0]);
                }
            }
        });

        idToGetFromWeb = idToGetFromWeb.map(id => id.substring(0, 5))
        idtoGetFromStorage = idtoGetFromStorage.map(id => id.substring(0, 5))

        let storageTLEPairs;
        let storageTLE = [];
        try {
            storageTLEPairs = await AsyncStorage.multiGet(idtoGetFromStorage);
        } catch(e) {
            console.log("Error happened when retrieving value of TLEs");
            console.log(e);
        }

        storageTLEPairs.forEach(pair => {
            if (pair[1] === null) {
                // Shouldnt happen
                idToGetFromWeb.push(pair[0]);
            } else {
                storageTLE.push(pair[1]);
            }
        });

        let data = [];
        if (idToGetFromWeb.length > 0) {
            data = await this.ST.getTLEs(idToGetFromWeb);

            // Convert TLE string to line separated array
            data =  data.split('\r\n');

            this.saveTLEsToAsyncStorage(data);
        }

        // storageTLE -> unfold form line1;line2
        storageTLE = this.formatTLEFromAsyncStorage(storageTLE);

        return [...storageTLE, ...data];
    }

    /**
     * Formats TLEs for using from data obtained from Async Storage.
     * 
     * @param {Array.<string>} data TLE data from Async Storage
     * 
     * @returns {Array.<string>} Formated TLE data
     */
    formatTLEFromAsyncStorage = (data) => {
        let arr = [];

        data.forEach(e => {
            let tle = e.split(';');
            arr.push(tle[0]);
            arr.push(tle[1]);
        });

        return arr;
    }

    /**
     * Saves TLE data to Async Storage in correct format.
     * 
     * @param {Array.<string>} data TLE data to store in Async Storage
     * 
     */
    saveTLEsToAsyncStorage = async (data) => {
        let pairsToSave = [];

        for (let i = 0; i < data.length - 1; i += 2) {
            let val = data[i] + ';' + data[i + 1];
            let id = data[i].substr(2,5);
            
            pairsToSave.push([id, val]);
        }

        let datePairs;
        // Timestamp in js is in ms 
        let currentTimestamp = Math.round((new Date()).getTime()).toString();

        datePairs = pairsToSave.map(pair => {
            return ([pair[0] + "Date", currentTimestamp]);
        });

        try {
            await AsyncStorage.multiSet([...pairsToSave, ...datePairs])
        } catch(e) {
            console.log("Error occured while saving data");
            console.log(e);
        }
    }

    /**
     * Parses downloaded TLE data into satellite objects and stops loading of data.
     * 
     * @returns {Array.<SatelliteObject>}
     */
    parseData = () => {
        let satelliteObjects = this.convertTLEtoSatelliteObjectCollection();
        this.loading = false;

        return satelliteObjects;
    }

    /**
     * Updates positions of currently visualized satellites.
     */
    updatePositions = () => {
        if (this.state.satellites.length > 0) {
            let copy = [...this.state.satellites]

            // Sometime there is error in position propagation according to satellite.js documentation
            // so its needed to store IDs of sats which will be later removed
            let removeIDs = [];

            copy.forEach((sat) => {
                try {
                    sat.updatePosition(new Date(this.clock.time()));
                } catch (error) {
                    console.log("ERROR");
                    console.log(error);
                    removeIDs.push(sat.id);
                    this.props.removeSatelliteCallback(sat.id);
                }
            });

            let cleaned = [...copy];
            removeIDs.forEach(id => {
                cleaned = cleaned.filter(sat => sat.id !== id);
            });

            this.setState({ satellites: cleaned });
        }

        // Update time of application
        this.props.setDateTimeCallback(new Date(this.clock.time()));
    }

    /**
     * Updates rotation of earth (satellites) according to real globe rotation.
     */
    updateRotation = () => {
        const rotationPerSecond = 0.00417807901;

        let now = this.clock.time();
        let difference = (now - this.lastRotationTime);

        let additionalRotation = difference * (rotationPerSecond / 1000);

        let rotation = (-this.state.groundRotationCompensation[1] + additionalRotation);
        rotation = rotation % 360;

        // Rotate Earth only if rotation is > 1 degreee (faster timescales, or long realtime visualization)
        if (rotation > 1) {
            this.setState({
                groundRotationCompensation: [0, -rotation, 0],
            });

            this.lastRotationTime = now;
        }
    }

    /**
     * Converts downloaded TLE data to SatelliteObjects array.
     * 
     * @returns {Array.<SatelliteObject>} Converted TLEs into SatelliteObjects
     */
    convertTLEtoSatelliteObjectCollection = () => {
        var tleIndex = 0;
        var idIndex = 0;
        var satrec;
        var satelliteObjects = [];

        // indexes are += 2 because each TLE has 2 lines
        for (tleIndex = 0; tleIndex < this.tle.length - 1; tleIndex += 2) {
            satrec = satellite.twoline2satrec(this.tle[tleIndex], this.tle[tleIndex + 1]);
            satelliteObjects.push(new SatelliteObject(satrec.satnum, satrec, [this.tle[tleIndex], this.tle[tleIndex + 1]]))
            idIndex += 1;
        }

        return satelliteObjects;
    }

    /**
     * Processes click on visualized satelite.
     * 
     * @param {string} id ID of clicked satellite
     */
    onModelClick = (id) => {
        let satelliteObject = this.state.satellites.filter((sat) => {
            return sat.id == id;
        })

        this.props.satelliteClickCallback(satelliteObject);
    }

    /**
     * Gets material for ground segment visualiziaton based on its ID.
     * 
     * @param {number} segmentID ID of ground segment
     * 
     * @returns {string} Material name
     */
    getMaterialForGroundSegment = (segmentID) => {
        if (segmentID === 11) {
            return "red";
        } else if (segmentID === 12) {
            return "gold";
        } else if (segmentID >= 13 && segmentID <= 16) {
            return "green";
        } else if (segmentID >= 21 && segmentID <= 26) {
            return "blue";
        } else if (segmentID >= 31 && segmentID <= 37) {
            return "yellow";
        } else if (segmentID >= 41 && segmentID <= 50) {
            return "purple";
        }
    }

    /**
     * Gets position of ground segment with given ID for visualiziation.
     * 
     * @param {string} segmentID ID of segment
     * 
     * @returns {Array.<number>} Position in ViroCoords
     */
    getPositionForGroundSegment = (segmentID) => {
        let denominator;

        if (this.props.flatTarget) {
            denominator = 52000;
        } else {
            denominator = 38500;
        }

        let city = groundSegmentIDtoCity[segmentID];
        let coordsOriginal = groundSegmentEciCoords[city];

        let x = coordsOriginal.x / denominator;
        let y = coordsOriginal.y / denominator;
        let z = coordsOriginal.z / denominator;

        // Conversion from ECI to ViroCoords
        return [y, z, x];
    }

    /**
     * Decides whether enough time, one rotation, has passed for satellite orbit to be updated.
     * 
     * @param {string} satID ID of satellite orbit to be checked
     * @param {Date} orbitTime Current time 
     * 
     * @returns {boolean} Decision whether to update or not
     */
    shouldUpdateOrbit = (satID, orbitTime) => {
        if (!(satID in this.orbitTimestamps)) {
            return true;
        }

        let maxtime = new Date(this.orbitTimestamps[satID]);
        maxtime.setMinutes(maxtime.getMinutes() + orbitTime);

        if (this.clock.time() > maxtime) {
            return true;
        }

        return false;
    }

    /**
     * Returns rotation of Earth according to ECI coordinates.
     * (Angle between ECI and ECEF x axis)
     * 
     * @returns {Array.<number>} Rotation vector
     */
    getEarthRotationForSatellites = () => {
        let gmst = satellite.gstime(new Date(this.clock.time()));
        let earth_orientation = {'x':1, 'y': 0, 'z': 0 };
        let earth_orientation_ECI = satellite.ecfToEci(earth_orientation, gmst);
        let earth_angle = Math.atan2(earth_orientation_ECI.y, earth_orientation_ECI.x);

        let angleDeg = earth_angle * (180/Math.PI);
        
        return [0, -angleDeg, 0];
    }

    /**
     * Maps selected ground segment IDs to models for render.
     * 
     * @returns {Array.<ViroSphere>} Array of 3D spheres as position visualization
     */
    getGroundSegmentToRender = () => {
        let segmentList;

        if (!this.loading) {
            segmentList = this.props.groundSegmentIDs.map((segmentID) => {
                let materialName = this.getMaterialForGroundSegment(segmentID);
                let position = this.getPositionForGroundSegment(segmentID);

                return (
                    <ViroSphere
                        key={segmentID}
                        heightSegmentCount={8}
                        widthSegmentCount={8}
                        radius={0.006}
                        position={position}
                        materials={[materialName]}
                    />
                );
            });
        }

        return segmentList;
    }

    /**
     * Maps selected satellite IDs to models for render.
     * 
     * @returns {Array.<ViroSphere|Viro3DObject>} Array of 3D models as satellite visualization
     */
    getSatellitesToRender = () => {
        let modelList;

        if (!this.loading) {
            modelList = this.state.satellites.map((sat) => {
                if (sat.description === "DEFAULT") {
                    return (
                        <ViroSphere
                            key={sat.id}
                            heightSegmentCount={10}
                            widthSegmentCount={10}
                            radius={0.01}
                            position={sat.position}
                            materials={["gray"]}

                            highAccuracyEvents={false}
                            onClick={() => {
                                this.onModelClick(sat.id);
                            }}

                            renderingOrder={10}
                        />
                    );
                }

                return (
                    <Viro3DObject
                        key={sat.id}
                        source={sat.modelPath}
                        resources={sat.resources}
                        position={sat.position}
                        scale={sat.scale}
                        rotation={sat.rotation}
                        type={sat.modelType}

                        highAccuracyEvents={false}
                        onClick={() => {
                            this.onModelClick(sat.id);
                        }}

                        renderingOrder={10}
                    />
                )
            });
        }

        return modelList;
    }

    /**
     * Maps selected satellite IDs (without excluded ones) to orbits for render.
     * 
     * @returns {Array.<ViroPolyline>} Array of Viro Polylines - orbits
     */
    getOrbitsToRender = () => {
        let orbitList;

        if (!this.loading) {
            orbitList = this.state.satellites.map((sat) => {
                let orbitTime = sat.getOrbitTime();
                if (!this.state.excludedOrbits.includes(sat.id)) {
                    let positions;

                    if (this.shouldUpdateOrbit(sat.id, orbitTime)) {
                        positions = sat.getPointsForOrbit(this.orbitSegmentCount, new Date(this.clock.time()));

                        this.orbitPoints[sat.id] = positions;
                        this.orbitTimestamps[sat.id] = this.clock.time();
                    } else {
                        positions = this.orbitPoints[sat.id];
                    }

                    return (
                        <ViroPolyline
                            key={sat.id}
                            position={[0, 0, 0]}
                            points={positions}
                            thickness={0.001}
                            materials={"red"}
                            opacity={this.state.orbitOpacity}
                        />
                    );
                }
            });
        }

        return orbitList;
    }

    /**
     * Renders flat target version of augumented globe.
     * 
     * @returns {ViroNode} Globe with all satelites and orbits.
     */
    renderFlatTargetGlobe = () => {
        let modelList = this.getSatellitesToRender();
        let groundSegmentList = this.getGroundSegmentToRender();
        let orbitsList = this.getOrbitsToRender();

        let rot = this.getEarthRotationForSatellites();

        return (
            <ViroNode position={[0, 0.2, 0]}>
                <ViroAmbientLight color="#FFFFFF" intensity={2000} temperature={4000} />

                <Viro3DObject source={require('../res/models/baseEarth/earth.obj')}
                    resources={[require('../res/models/baseEarth/earth_mat.mtl'),
                    require('../res/models/baseEarth/earth_texture.png')]}
                    position={[0.0, 0.0, 0.0]}
                    scale={[0.06, 0.06, 0.06]}
                    rotation={[0, 0, 0]}
                    type="OBJ"
                />  

                <ViroNode rotation={[0, 0, 0]}>
                    <ViroNode rotation={rot}>
                        {modelList}
                        {orbitsList}
                    </ViroNode>
                </ViroNode>
                
                <ViroNode rotation={this.modelListRotation}>
                    {groundSegmentList}
                </ViroNode>
            </ViroNode>
        );
    }

    /**
     * Renders full 3D model of Earth or only its transparent version but with occlusive properties. 
     * 
     * @returns {<Viro3DObject|ViroNode} Globe for globe target
     */
    renderVirtualGlobe = () => {
        if (this.props.renderVirtualGlobe) {
            return (
                <Viro3DObject source={require('../res/models/baseEarth/earth.obj')}
                    resources={[require('../res/models/baseEarth/earth_mat.mtl'),
                    require('../res/models/baseEarth/earth_texture.png')]}
                    position={[0.0, 0.0, 0.0]}
                    scale={[0.08, 0.08, 0.08]}
                    rotation={[180, -150, -180]}  // Rotated that africa is towards camera
                    type="OBJ"
                />
            );
        } else {

            return (
                <ViroNode>
                    <ViroSphere
                        heightSegmentCount={20}
                        widthSegmentCount={20}
                        radius={0.17}
                        position={[0, 0, 0]}
                        materials={["occlusive"]}
                        renderingOrder={-1}
                    />

                    <Viro3DObject source={require('../res/models/ghostEarth/earth.obj')}
                        resources={[require('../res/models/ghostEarth/mat.mtl'),
                        require('../res/models/ghostEarth/blank.png')]}
                        position={[0.0, 0.0, 0.0]}
                        scale={[0.18, 0.18, 0.18]}
                        rotation={[0, -175, 0]}  // rotated that africa is towards camera
                        type="OBJ"
                        opacity={0.3}
                    />
                </ViroNode>
            );
        }
    }

    /**
     * Renders globe target version of augumented globe.
     * 
     * @returns {ViroNode} Globe with all satelites and orbits.
     */
    renderGlobeTargetGlobe = () => {
        let modelList = this.getSatellitesToRender();
        let groundSegmentList = this.getGroundSegmentToRender();
        let orbitsList = this.getOrbitsToRender();

        let rot = this.getEarthRotationForSatellites();

        return (
            <ViroNode>
                <ViroAmbientLight color="#FFFFFF" />

                {this.renderVirtualGlobe()}

                <ViroNode rotation={rot}>
                    {modelList}
                    {orbitsList}
                </ViroNode>

                <ViroNode rotation={this.modelListRotation}>
                    {groundSegmentList}
                </ViroNode>
            </ViroNode>
        );
    }

    /**
     * Decides whether to render flat or globe target globe.
     * 
     * @returns {ViroNode} Correct globe with all orbits, satellites and ground segments.
     */
    renderGlobe = () => {
        if (this.props.flatTarget) {
            return this.renderFlatTargetGlobe();
        } else {
            return this.renderGlobeTargetGlobe();
        }
    }

    /**
     * Renders whole component - visualization
     * 
     * @returns {ViroNode} Globe visualization
     */
    render() {
        return (
            <ViroNode>
                {this.renderGlobe()}
            </ViroNode>
        );
    }
}

/**
 * ECI cooardinates of cities with GPS ground segments.
 * 
 * @constant
 * @type {Object<string, Object<string, number>>}
 */
const groundSegmentEciCoords = {
    "schriever": { "x": 3910.3096787548093, "y": 3080.3510329695946, "z": 3975.5813422691904 },
    "vandenberg": { "x": 4856.790504752817, "y": 1985.1370670602403, "z": 3615.737268949631 },
    "cape": { "x": 2619.008345543573, "y": 4961.851777391096, "z": 3024.8968340995298 },
    "ascension": { "x": -3922.7604262692257, "y": 4952.862829619637, "z": -875.6544312352279 },
    "diego": { "x": -5170.553732498662, "y": -3647.0385227647125, "z": -806.8053933403049 },
    "kwajalein": { "x": 4093.666061017764, "y": -4796.201022830874, "z": 960.7141097469664 },
    "hawaii": { "x": 5786.235355097538, "y": -1500.9790587172447, "z": 2219.0532011295636 },
    "greenland": { "x": 410.97339412324754, "y": 1434.4349979707606, "z": 6181.170740804261 },
